import { Component } from "@angular/core";

import { products } from "../products";

@Component({
  selector: "app-product-list",
  templateUrl: "./product-list.component.html",
  styleUrls: ["./product-list.component.css"],
})
export class ProductListComponent {
  products = products;

  addtoChart() {
    window.alert("The product has been Added to Cart !");
  }

  onNotify() {
    window.alert("You will be notified when the product goes on sale");
  }
}
<div class="row">
    <div class="col-md-4" *ngFor="let product of products">
        <h3>
        <h3>
            <a [title]="product.name + ' details'" [routerLink]="['/products', product.id]">
                {{ product.name }}
            </a>
        </h3>
        </h3>

        <p *ngIf="product.description">
            Description: {{ product.description }}
        </p>

        <button type="button" (click)="addtoChart()" class="">
            <i class="material-icons text-white">shopping_cart_checkout</i>  Add to Cart
        </button>
        <app-product-alerts [product]="product"></app-product-alerts>
    </div>
</div>
